            //         { source: {uri: imagesUrl[0]}},
            //         { source: {uri: imagesUrl[1]}},
            //         { source: {uri: imagesUrl[2]}}
            //     ]}
            // />
            <Modal visible={true} transparent={true}>
                <ImageViewer imageUrls={images}/>
            </Modal>
        );
    }
}

